  selector: 'app-blog-post',
  templateUrl: './blog-post.component.html',
  styleUrls: ['./blog-post.component.css']
})
export class BlogPostComponent {

  constructor(public ui: UiService) { }

  @Input() blogPost: BlogPost | undefined

